mapper.select:
/** ieda.iaas.information.iaasConfig.selectIaasAccountDuplicationByConfigInfo **/
    Select count(*)
      From ieda_iaas_config
    Where iaas_type=#{config.iaasType}


            And common_region = #{config.commonRegion}
            And common_availability_zone = #{config.commonAvailabilityZone}
            And common_keypair_name = #{config.commonKeypairName}
            And common_keypair_path = #{config.commonKeypairPath} 


            And common_security_group = #{config.commonSecurityGroup}
            And common_keypair_name = #{config.commonKeypairName}
            And common_keypair_path = #{config.commonKeypairPath}  


            And common_keypair_path = #{config.commonKeypairPath}
            And google_public_key = #{config.googlePublicKey}


            And vsphere_vcenter_vm_folder = #{config.vsphereVcenterVmFolder}
            And vsphere_vcenter_template_folder = #{config.vsphereVcenterTemplateFolder}
            And vsphere_vcenter_datastore = #{config.vsphereVcenterDatastore} 
            And vsphere_vcenter_persistent_datastore = #{config.vsphereVcenterPersistentDatastore} 
            And vsphere_vcenter_disk_path = #{config.vsphereVcenterDiskPath} 
            And vsphere_vcenter_cluster = #{config.vsphereVcenterCluster}  


            And common_security_group = #{config.commonSecurityGroup}
            And azure_resource_group = #{config.azureResourceGroup}
            And azure_storage_account_name = #{config.azureStorageAccountName}
            And azure_ssh_public_key = #{config.azureSshPublicKey}

/** ieda.iaas.information.iaasConfig.selectIaasAccountDuplicationByAccountName **/
    Select count(*)
      From ieda_iaas_config
    Where iaas_type = #{config.iaasType}
       And iaas_config_alias = #{config.iaasConfigAlias}

mapper.update:
/** ieda.iaas.information.iaasConfig.updateIaasConfigInfo **/
 Update ieda_iaas_config
 Set
    iaas_config_alias = #{config.iaasConfigAlias}
    ,account_id = #{config.accountId}
    ,common_security_group = #{config.commonSecurityGroup}
    ,common_region = #{config.commonRegion}
    ,common_keypair_name = #{config.commonKeypairName}
    ,common_keypair_path = #{config.commonKeypairPath}
    ,common_availability_zone = #{config.commonAvailabilityZone}
    ,google_public_key = #{config.googlePublicKey}
    ,vsphere_vcenter_datacenter_name = #{config.vsphereVcentDataCenterName}
    ,vsphere_vcenter_vm_folder = #{config.vsphereVcenterVmFolder}
    ,vsphere_vcenter_template_folder = #{config.vsphereVcenterTemplateFolder}
    ,vsphere_vcenter_datastore = #{config.vsphereVcenterDatastore}
    ,vsphere_vcenter_persistent_datastore = #{config.vsphereVcenterPersistentDatastore}
    ,vsphere_vcenter_disk_path = #{config.vsphereVcenterDiskPath}
    ,vsphere_vcenter_cluster = #{config.vsphereVcenterCluster}
    ,azure_resource_group = #{config.azureResourceGroup}
    ,azure_storage_account_name = #{config.azureStorageAccountName}
    ,azure_ssh_public_key = #{config.azureSshPublicKey}
    ,azure_private_key = #{config.azurePrivateKey}
    ,update_user_id = #{config.createUserId}
    ,update_date = now()
 Where
 id = #{config.id};

mapper.delete:
/** ieda.iaas.information.iaasConfig.deleteIaasConfigInfo **/
    Delete From ieda_iaas_config
  Where create_user_id = #{createUserId}
      And id = #{id}
